async function confirm(message, author, validReactions, time = 60000) {
    if(!message) throw new ReferenceError('"message" is not initialized')
    if(!validReactions || validReactions.length !== 2) throw new ReferenceError('Invalid form body')
    if(typeof time !== "number") throw new SyntaxError('"time" must be a number')
    if(!message.guild.me.hasPermission('MANAGE_MESSAGES')) return console.log(`"MANAGE_MESSAGES" permission is needed to the client.`)

    for (const reaction of validReactions) await message.react(reaction)

    const seperate = (reaction, user) => validReactions.includes(reaction.emoji.name) && user.id === author.id

    return message
      .awaitReactions(seperate, { max: 1, time: time })
      .then((collected) => collected.first() && collected.first().emoji.name);
}

module.exports = confirm;