async function deletetimeout(message, messageToDelete, time = 10000) {
    if(!message) throw new ReferenceError('"message" is not specified')
    if(typeof time !== "number") throw new SyntaxError('"time" must be a number')
    if(!message.guild.me.hasPermission("MANAGE_MESSAGES")) return console.log(`<client> needs "MANAGE_MESSAGES" permission`)
    messageToDelete.react('❌')
    const seperate = (reaction, user) => {
        return reaction.emoji.name === '❌' && user.id === message.author.id;
    };

    messageToDelete.awaitReactions(seperate, { max: 1, time: time, errors: ['time']})
    .then(collected => {
        messageToDelete.delete()
    })
    .catch(err => {
        messageToDelete.reactions.removeAll()
    });

}

module.exports = deletetimeout;